import React, { useState } from 'react';
import LoginForm from './components/LoginForm';

function App() {
  // Track whether the user is logged in and their role
  const [role, setRole] = useState(null);

  // If no role yet, show login
  if (!role) {
    return (
      <div>
        <LoginForm onLoginSuccess={setRole} />
      </div>
    );
  }

  // After login, show a placeholder message
  return (
    <div style={{ textAlign: 'center', marginTop: '2rem' }}>
      <h2>Welcome, {role}!</h2>
      <p>This is where your {role === 'admin' ? 'Admin Dashboard' : 'Employee Dashboard'} will go.</p>
    </div>
  );
}

export default App;
