import React, { useState } from 'react';

// This component receives a function prop called onLoginSuccess
export default function LoginForm({ onLoginSuccess }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // For now, just check if username and password are not empty
    if (username === '' || password === '') {
      setError('Please enter both username and password.');
      return;
    }

    // Mock login logic (we will replace this with real API later)
    if (username === 'employee' && password === 'password') {
      onLoginSuccess('employee');
    } else if (username === 'admin' && password === 'password') {
      onLoginSuccess('admin');
    } else {
      setError('Invalid credentials.');
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ maxWidth: '300px', margin: '2rem auto' }}>
      <h2>Login</h2>
      <input
        type="text"
        placeholder="Username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
        style={{ width: '100%', marginBottom: '0.5rem' }}
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        style={{ width: '100%', marginBottom: '0.5rem' }}
      />
      <button type="submit" style={{ width: '100%' }}>Login</button>
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </form>
  );
}
